import React, { Component, Suspense, lazy } from "react";
import { BrowserRouter as Router, Redirect, Switch, Route } from "react-router-dom";
import LandingPage from "./components/landing-page/landing-page";
import UXDesigns from "./components/ux-designs/ux-designs";
import Projects from "./components/projects/Projects";
import Courses from "./components/courses/courses";
import AboutMe from "./components/about-me/about-me";
import PageNotFound from "./components/page-not-found/PageNotFound";
import "./App.css";
// import SinglePageItem from "./components/single-page-item/SinglePageItem";
import projects from "../src/data/project-data";
import landingPageBG from "./landing-video/landing-bg.png";

// const LandingPage = lazy(() =>
// 	import("./components/landing-page/landing-page")
// );
// const UXDesigns = lazy(() => import("./components/ux-designs/ux-designs"));
// const Projects = lazy(() => import("./components/projects/Projects"));
// const Courses = lazy(() => import("./components/courses/courses"));
// const AboutMe = lazy(() => import("./components/about-me/about-me"));
const SinglePageItem = lazy(() =>
	import("./components/single-page-item/SinglePageItem")
);

class App extends Component {
	constructor(props) {
		super(props);
		this.state = {
			projects: null,
		};
	}

	render() {
		return (
			<Router className={"app"}>
				<Switch>
					<>
						<Suspense
							fallback={
								<div>
									{/* <div className="landing-page-wrapper"> */}
										{/* Quotes can change every few seconds */}
										{/* <nav /> */}
										{/* <main className="landing-main-wrapper"> */}
											<img
												className="loader-landing-page-img"
												src={landingPageBG}
											/>
											{/* <ProfileCard /> */}
											{/* <a to="/projects"> */}
												{/* <button className="landing-page-a"> */}
													{/* Enter */}
												{/* </button> */}
											{/* </a> */}
										{/* </main> */}
									{/* </div> */}
								</div>
							}
						>
							<Route path="/" exact component={LandingPage} />
							<Route
								path="/projects"
								exact
								component={Projects}
							/>
							<Route
								path="/uxdesigns"
								exact
								component={UXDesigns}
							/>
							<Route path="/courses" exact component={Courses} />
						</Suspense>
						<Suspense
							fallback={
								<>
									<div className="page-wrapper">
										<div className="page-width">
											<header>
												<nav className="nav">
													<div className="short-nav">
														<div className="logo-container"></div>
														<div className="collasped-menu-btn"></div>
														<ul className="nav-content">
															<a className="link lg-display">
																<li className="nav-item project">
																	<div className="loader-bg">
																		<div className="loader-size-wrapper indent">
																			<a className="button"></a>
																			<div className="loader"></div>
																		</div>
																	</div>
																</li>
															</a>
															<a className="link lg-display">
																<li className="nav-item ux">
																	<div className="loader-bg">
																		<div className="loader-size-wrapper indent">
																			<a
																				className="button"
																				name="project"
																			></a>
																			<div className="loader"></div>
																		</div>
																	</div>
																</li>
															</a>
															<a className="link lg-display">
																<li className="nav-item courses">
																	<div className="loader-bg">
																		<div className="loader-size-wrapper indent">
																			<a
																				className="button"
																				name="project"
																			></a>
																			<div className="loader"></div>
																		</div>
																	</div>
																</li>
															</a>
															<a className="link lg-display">
																<li className="nav-item about-me">
																	<div className="loader-bg">
																		<div className="loader-size-wrapper indent">
																			<a
																				className="button"
																				name="project"
																			></a>
																			<div className="loader"></div>
																		</div>
																	</div>
																</li>
															</a>
														</ul>
													</div>
												</nav>
											</header>
											<main className="main-wrapper">
												<h1>About Me</h1>
												<div className="about-me-wrapper">
													<img className="about-me-img" />
													<a className="download">
														<button>
															Download CV
														</button>
													</a>
													<nav className="social-nav-btns"></nav>
													<p></p>
													<p></p>
													<p></p>
													<p></p>
												</div>
											</main>
											<div className="footer-wrapper">
												footer
											</div>
										</div>
									</div>
								</>
							}
						>
							<Route path="/aboutme" exact component={AboutMe} />
						</Suspense>
						<Suspense fallback={<div>Loading...</div>}>
							<Route
								path="/projects/:id"
								exact
								component={SinglePageItem}
							/>
							<Route
								path="/uxdesigns/:id"
								exact
								component={SinglePageItem}
							/>
							<Route
								path="/courses/:id"
								exact
								component={SinglePageItem}
							/>
						</Suspense>
							<Route path="" component={PageNotFound} />
							{/* <Redirect path="/404" /> */}
					</>
				</Switch>
			</Router>
		);
	}
}

export default App;
